import ReactSlider from "react-slider";
import "./Slider.css";
import SettingsContext from "./SettingsContext";
import BackButton from "./BackButton";
import { useState, useEffect, useRef, useContext } from "react";
import DarkModeButton from "./DarkModeButton";
import LightModeButton from "./LightModeButton";

function Settings() {
  const settingsInfo = useContext(SettingsContext);

  const [setDark, setToDarkMode] = useState(null)
  const [setLight, setToLightMode] = useState(true)

  const setLightRef = useRef(setToLightMode);

  const click = setDark => {
    setToDarkMode(setDark)
  }

  useEffect(() => {
    document.body.style.backgroundColor = setDark
  }, [setDark])

  return (
    <>
    <div>
      {setLight ? (
      <DarkModeButton onClick = {() => {
      click("#253834");
      setToLightMode(false);
      setLightRef.current = false;}}
      />
      ) : (
    < LightModeButton onClick = {() => {
      click("#efe6d5");
      setToLightMode(true);
      setLightRef.current = true;}}
      />
      )}
      </div>

    <div style={{ textAlign: "left" }}>
      <h1 className="work">Work: {settingsInfo.workMinutes}:00</h1>
      <ReactSlider
        className={"sliderWork"}
        thumbClassName={"thumbWork"}
        trackClassName={"track"}
        value={settingsInfo.workMinutes}
        onChange={(newValue) => settingsInfo.setWorkMinutes(newValue)}
        min={1}
        max={120}
      />
      <h1 className="break">Break: {settingsInfo.breakMinutes}:00</h1>
      <ReactSlider
        className={"sliderBreak"}
        thumbClassName={"thumbBreak"}
        trackClassName={"track"}
        value={settingsInfo.breakMinutes}
        onChange={(newValue) => settingsInfo.setBreakMinutes(newValue)}
        min={1}
        max={120}
      />
      <div style={{ textAlign: "center", marginTop: "20px" }}>
        <BackButton onClick={() => settingsInfo.setShowSettings(false)} />
      </div>
    </div>
    </>
  );
}
export default Settings;
