import "./App.css";
import Timer from "./Timer";
import Settings from "./Settings";
import { useState, useRef } from "react";
import SettingsContext from "./SettingsContext";
import DarkModeButton from "./DarkModeButton";
import LightModeButton from "./LightModeButton";


function App() {
  const [showSettings, setShowSettings] = useState(false);
  const [workMinutes, setWorkMinutes] = useState(45);
  const [breakMinutes, setBreakMinutes] = useState(15);

  const [setToDark, setDark] = useState("#efe6d5");
  const setToDarkRef = useRef(setToDark);

  const click = setToDark => {
    setDark(setToDark)
  }

  useEffect(() => {
    document.body.style.backgroundColor = setToDark
  }, [setToDark])

  return (
    <main>
      <div>
      {setToDark ? (
            <DarkModeButton
              onClick={() => {
                setDark(false);
                setToDarkRef.current = false;
              }}
            />
          ) : (
            <LightModeButton
              onClick={() => {
                setDark(true);
                setToDarkRef.current = true;
              }}
            />
          )}
          </div>

      <SettingsContext.Provider
        value={{
          showSettings,
          setShowSettings,
          workMinutes,
          breakMinutes,
          setWorkMinutes,
          setBreakMinutes,
        }}
      >
        {showSettings ? <Settings /> : <Timer />}
      </SettingsContext.Provider>
    </main>
  );
}

export default App;
