import "./App.css";
import Timer from "./Timer";
import Settings from "./Settings";
import { useState, useEffect, useRef } from "react";
import SettingsContext from "./SettingsContext";
import DarkModeButton from "./DarkModeButton";
import LightModeButton from "./LightModeButton";
//import LightModeButton from "./LightModeButton";


function App() {
  const [showSettings, setShowSettings] = useState(false);
  const [workMinutes, setWorkMinutes] = useState(45);
  const [breakMinutes, setBreakMinutes] = useState(15);

  const [setDark, setToDarkMode] = useState("#efe6d5")
  const [setLight, setToLightMode] = useState(true)

  const setLightRef = useRef(setToLightMode);

  const click = setDark => {
    setToDarkMode(setDark)
  }

  useEffect(() => {
    document.body.style.backgroundColor = setDark
  }, [setDark])





  return (
    <main>
      <div>
      {setLight ? (
      <DarkModeButton onClick = {() => {
      click("#0c0c0b");
      setToLightMode(false);
      setLightRef.current = false;}}
      />
      ) : (
    < LightModeButton onClick = {() => {
      click("#efe6d5");
      setToLightMode(true);
      setLightRef.current = true;}}
      />
      )}
      </div>

      <SettingsContext.Provider
        value={{
          showSettings,
          setShowSettings,
          workMinutes,
          breakMinutes,
          setWorkMinutes,
          setBreakMinutes,
        }}
      >
        {showSettings ? <Settings /> : <Timer />}
      </SettingsContext.Provider>
    </main>
  );
}

export default App;
